/**
 * @module DOMCache
 * @description React-optimized DOM element caching with ref management
 */

import { useRef, useEffect } from 'react';

export const createElementCache = () => {
    const cache = new Map();
    
    const getElement = (selector) => {
        if (typeof selector !== 'string') return null;
        
        if (!cache.has(selector) || !cache.get(selector).isConnected) {
            const element = document.querySelector(selector);
            if (element) cache.set(selector, element);
        }
        
        return cache.get(selector) || null;
    };

    return { getElement };
};

export const useElementRef = (selector) => {
    const ref = useRef(null);
    
    useEffect(() => {
        if (!selector) return;
        ref.current = document.querySelector(selector);
    }, [selector]);
    
    return ref;
};
