/**
 * @module DOMCache
 * @description Provides a proxy-based caching mechanism for DOM elements
 * @type {Proxy<Object, ProxyHandler>}
 */

export const DOMCache = new Proxy({}, {
    /**
     * @function get
     * @param {Object} cache - The cache object
     * @param {string} selector - CSS selector for the element
     * @returns {Element|null} Cached DOM element or null if not found
     */
    get: (cache, selector) => {
        if (typeof selector !== 'string') return undefined;
        if (!cache[selector]?.isConnected) {
            cache[selector] = document.querySelector(selector);
        }
        return cache[selector];
    }
});
