import { CoreService } from '../services/CoreService.js';
import { RenderingModule } from './RenderingModule.js';
import { DOMCache } from './DOMCache.js';
import { Config } from '../config/config.js';
import { ScheduleManager } from './ScheduleManager.js';
import mainFloorImage from '../elements/mainfloorcrunched.png';
import combFloorImage from '../elements/combscaled.png';

/**
 * @module EventHandlingModule
 * @description Simplified event handling for user interactions
 */
export const EventHandlingModule = {
    /**
     * @function displayNextClass
     * @description Displays the next class from schedule
     */
    displayNextClass() {
        try {
            // Get schedule from CoreService or directly from ScheduleManager
            let schedule = CoreService.getState('schedule');
            
            // If no schedule is in state, get it from ScheduleManager
            if (!schedule || !Array.isArray(schedule) || schedule.length === 0) {
                schedule = ScheduleManager.getCurrentSchedule();
                // Store for future use
                CoreService.setState('schedule', schedule);
            }
            
            if (!schedule || !schedule.length) {
                console.log("No schedule available");
                return false;
            }
            
            // Find the next class
            const now = new Date();
            const nextClass = schedule.find(cls => new Date(cls.startTime) > now) || schedule[0];
            
            if (nextClass && nextClass.room) {
                // Set room in UI and prepare navigation
                const startInput = document.getElementById("start");
                const endInput = document.getElementById("end");
                
                if (startInput && endInput) {
                    // Use current location as start if available, otherwise use a default
                    const currentLocation = CoreService.getState('currentLocation') || "MAIN";
                    startInput.value = currentLocation;
                    endInput.value = nextClass.room;
                    
                    // Trigger navigation if auto-navigate is enabled
                    if (CoreService.getState('autoNavigate')) {
                        this.handlePathRequest();
                    }
                    
                    return true;
                }
            }
            
            return false;
        } catch (error) {
            console.error("Error displaying next class:", error);
            return false;
        }
    },

    /**
     * @function handlePathRequest
     * @async
     * @description Processes navigation requests between rooms
     */
    async handlePathRequest() {
        const start = document.getElementById("start")?.value?.toUpperCase();
        const end = document.getElementById("end")?.value?.toUpperCase();
        
        const result = await CoreService.findPath(start, end);
        
        if (!result.success) {
            alert(result.error);
            RenderingModule.refresh();
            return;
        }
        
        // Update UI with new path
        RenderingModule.renderPath(result.path);
        CoreService.setState('pathProgress', 0);
        this.initializeAgentMovement();
    },
    
    /**
     * @function initializeAgentMovement
     * @description Sets up agent for path following
     */
    initializeAgentMovement() {
        // Clear any existing animation
        if (CoreService.getState('animationId')) {
            cancelAnimationFrame(CoreService.getState('animationId'));
        }
        
        // Initialize position
        CoreService.updateAgentPosition(0);
        
        // Center view on agent
        const agentElement = DOMCache[Config.SVG.SELECTORS.AGENT];
        if (agentElement) {
            this.centerViewOnElement(agentElement);
        }
    },
    
    /**
     * @function centerViewOnElement
     * @description Centers the viewport on a specific element
     * @param {Element} element - DOM element to center on
     */
    centerViewOnElement(element) {
        if (!element) return;
        
        const svgdiv = DOMCache[Config.SVG.SELECTORS.SVGDIV];
        if (!svgdiv) return;
        
        const rect = element.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;
        
        svgdiv.scrollLeft = centerX - svgdiv.clientWidth / 2;
        svgdiv.scrollTop = centerY - svgdiv.clientHeight / 2;
    },
    
    /**
     * @function setupEventListeners
     * @description Initializes all event listeners for UI interactions
     */
    setupEventListeners() {
        // Navigation button
        const navButton = document.getElementById('navigate-button');
        if (navButton) {
            navButton.addEventListener('click', () => this.handlePathRequest());
        }
        
        // Floor switch buttons
        const mainFloorBtn = document.getElementById('main-floor-btn');
        const combFloorBtn = document.getElementById('comb-floor-btn');
        
        if (mainFloorBtn) {
            mainFloorBtn.addEventListener('click', () => {
                RenderingModule.switchFloor('main', mainFloorImage);
            });
        }
        
        if (combFloorBtn) {
            combFloorBtn.addEventListener('click', () => {
                RenderingModule.switchFloor('comb', combFloorImage);
            });
        }
        
        // Schedule button
        const scheduleBtn = document.getElementById('schedule-btn');
        if (scheduleBtn) {
            scheduleBtn.addEventListener('click', () => this.displayNextClass());
        }
    }
};
