/**
 * @module PathfindingModule
 * @description Handles pathfinding algorithms and route calculations between rooms
 */

export const PathfindingModule = {
    /**
     * @function constructPath
     * @param {Array<Array<number>>} nextMatrix - Next vertex matrix for path reconstruction
     * @param {number} start - Starting vertex index
     * @param {number} end - Ending vertex index
     * @returns {number[]} Array of vertex indices forming the path
     */
    constructPath(nextMatrix, start, end) {
        if (!nextMatrix[start]?.[end]) return [];
        const path = [start];
        while (path[path.length - 1] !== end) {
            const next = nextMatrix[path[path.length - 1]][end];
            if (next === undefined) break;
            path.push(next);
        }
        return path;
    },

    /**
     * @function selectBestNode
     * @param {number[]} nodes - Array of candidate nodes
     * @param {number[]} goalNodes - Array of possible goal nodes
     * @param {Array<Array<number>>} distMatrix - Distance matrix between nodes
     * @returns {number|null} Best node index or null if no valid node found
     */
    selectBestNode(nodes, goalNodes, distMatrix) {
        if (!nodes?.length || !goalNodes?.length) return null;
        return nodes.reduce((best, node) => {
            const minDist = Math.min(...goalNodes.map(goal => 
                distMatrix[node]?.[goal] ?? Infinity
            ));
            return minDist < best.dist ? { node, dist: minDist } : best;
        }, { node: null, dist: Infinity }).node;
    },

    /**
     * @function findShortestPath
     * @param {Array<Array<number>>} nextMatrix - Next vertex matrix
     * @param {Array<Array<number>>} distMatrix - Distance matrix
     * @param {string} startRoom - Starting room identifier
     * @param {string} endRoom - Ending room identifier
     * @param {Object.<string, number[]>} rooms - Room to vertices mapping
     * @returns {number[]} Array of vertex indices forming the shortest path
     */
    findShortestPath(nextMatrix, distMatrix, startRoom, endRoom, rooms) {
        if (!rooms[startRoom] || !rooms[endRoom] || startRoom === endRoom) return [];

        const startNode = this.selectBestNode(rooms[startRoom], rooms[endRoom], distMatrix);
        if (startNode === null) return [];

        const endNode = this.selectBestNode(rooms[endRoom], [startNode], distMatrix);
        if (endNode === null) return [];

        return this.constructPath(nextMatrix, startNode, endNode);
    }
};
