/**
 * @module ColorModule
 * @description Manages color calculations for progress visualization
 */

export const ColorModule = {
    /**
     * @function getColor
     * @param {number} progress - Progress value between 0 and 1
     * @returns {string} RGB color string
     * @description Calculates color based on progress with caching
     */
    getColor: (() => {
        const colorCache = new Map();
        
        return (progress) => {
            const validProgress = Math.max(0, Math.min(1, Number(progress) || 0));
            const key = Math.round(validProgress * 100);
            
            if (!colorCache.has(key)) {
                if (validProgress >= 0.99) {
                    colorCache.set(key, 'rgb(0,255,0)');
                } else {
                    const green = Math.min(Math.floor(validProgress * 510), 255);
                    const red = validProgress >= 0.49 
                        ? Math.max(255 - Math.floor((validProgress - 0.49) * 510), 0)
                        : 255;
                    colorCache.set(key, `rgb(${red},${green},0)`);
                }
            }
            return colorCache.get(key);
        };
    })()
};
