const importAll = (r) => {
    const images = {};
    r.keys().forEach((key) => {
        const imagePath = key.replace('./', '');
        images[imagePath] = r(key).default || r(key); // Handle both ES modules and CommonJS
    });
    return images;
};

export const ImageModule = {
    streetViewImages: importAll(require.context('../elements/streetview', false, /\.(png|jpe?g|svg)$/)),
    getImageUrl: (imageName) => {
        const imageUrl = ImageModule.streetViewImages[imageName];
        if (!imageUrl) {
            console.warn(`Image not found: ${imageName}`);
        }
        return imageUrl;
    }
};
