import React, { useState, useEffect } from 'react';
import { useMsal } from "@azure/msal-react";
import { loginRequest } from "../src/Auth/authConfig";
import axios from 'axios';
import Navbar from "../src/Components/Navbar";
import AuthContext from "../src/Auth/AuthContext";
import { Navigation } from './pages/Navigation';
import { Schedule } from './pages/Schedule';
import { Settings } from './pages/Settings';

function App() {
  const [auth, setAuth] = useState({
    isAuth: false,
  });
  const [activeTab, setActiveTab] = useState(1);
  const { instance, accounts } = useMsal();

  useEffect(() => {
    if (accounts[0]) {
      instance
        .acquireTokenSilent({
          ...loginRequest,
          account: accounts[0],
        })
        .then((response) => {
          const resp = {...response.account, grade: parseInt(response.account.grade)}
          setAuth((prev) => ({
            ...prev,
            token: response.accessToken,
            user: resp,
          }));
          testToken(response.accessToken);
        });
    }
  }, [accounts]);

  const testToken = async (token) => {
    const res = await axios.get("https://graph.microsoft.com/v1.0/me", {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    });

    if (
      res.data.jobTitle === "12" ||
      res.data.jobTitle === "11" ||
      res.data.jobTitle ==="10" ||
      res.data.jobTitle === "9"
    ) {
      setAuth((prev) => ({
        ...prev,
        isAuth: true,
        user: {
          ...prev.user,
          role: "student",
          displayName: `${res.data.givenName} ${res.data.surname}`,
          grade: res.data.jobTitle,
        },
      }));
    }
  };

  const renderContent = () => {
    switch(activeTab) {
      case 1:
        return (
          <div id="content">
            {/* Your existing index.html content */}
            <div id="searchContainer">
              <input type="text" id="start" placeholder="Start Location"/>
              <input type="text" id="end" placeholder="End Location"/>
              <input type="button" onClick={() => window.markShortestPathFromInput()} value="Navigate"/>
            </div>
            <div id="svgdiv">
              <svg id="svg" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1308 2048">
                <g id="svgraph">
                  <g>
                    <image width="2048" height="1307" preserveAspectRatio="none" href="elements/mainfloorcrunched.png"></image>
                    <g id="graph"></g>
                  </g>
                </g>
              </svg>
            </div>
            <div id="scroll"><div></div></div>
            <input title="Progress" type="range" min="0" max="200" value="0" id="progbar"/>
          </div>
        );
      case 2:
        return <Schedule />;
      case 3:
        return <Settings />;
      default:
        return null;
    }
  };

  return (
    <AuthContext.Provider value={{ auth, setAuth }}>
      <Navbar 
        instance={instance}
        loginRequest={loginRequest}
        activeTab={activeTab}
        setActiveTab={setActiveTab}
      />
      {auth.isAuth ? 
        <div>
          {renderContent()}
        </div> :
        <div>
          {/* Login content */}
        </div>
      }
    </AuthContext.Provider>
  );
}

export default App;
