/**
 * @module ScheduleManager
 * @description Manages the storage and retrieval of room schedules by day.
 * Handles deduplication and persistence of schedule data in localStorage.
 */

export const ScheduleManager = {
    /**
     * @function save
     * @param {string} day - The day identifier for the schedule
     * @param {string[]} rooms - Array of room identifiers
     * @returns {string[]} Array of unique room identifiers
     * @throws {Error} If day is invalid or rooms is not an array
     * @description Saves a daily schedule of rooms with automatic deduplication
     */
    save(day, rooms) {
        if (!day || !Array.isArray(rooms)) return [];
        const uniqueRooms = [...new Set(rooms)];
        localStorage.setItem(`schedule_${day}`, JSON.stringify(uniqueRooms));
        return uniqueRooms;
    },

    /**
     * @function load
     * @param {string} day - The day identifier to load the schedule for
     * @returns {string[]} Array of room identifiers, empty if none found
     * @description Loads and deduplicates a daily schedule from storage
     */
    load(day) {
        const schedule = localStorage.getItem(`schedule_${day}`);
        return schedule ? [...new Set(JSON.parse(schedule))] : [];
    },

    /**
     * @function notifyUser
     * @param {string} message - The notification message
     * @description Dispatches a custom event for schedule-related notifications
     */
    notifyUser(message) {
        window.dispatchEvent(new CustomEvent('schedule-notification', { detail: { message } }));
    }
};
